mapper.select:
select count(id)
from dish
where category_id = #{categoryId}

select d.*,c.name categoryName from dish d left join category c on d.category_id = c.id


        and d.name like concat('%',#{name},'%')


        and d.category_id = #{categoryId}


        and d.status = #{status}


order by d.create_time desc

mapper.update:
update dish
set status = #{status}
where id = #{id}

mapper.select:
select * from dish


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}


order by create_time desc

select a.*,b.name dfName,b.value from dish a left join dish_flavor b on a.id = b.dish_id
and name like '%${name}%'
and category_id = #{categoryId}
and status = #{status}
