mapper.select:
select count(id)
from dish
where category_id = #{categoryId}

select d.*,c.name categoryName from dish d left join category c on d.category_id = c.id


        and d.name like concat('%',#{name},'%')


        and d.category_id = #{categoryId}


        and d.status = #{status}


order by d.create_time desc
